const NRContainer = require("./NRContainer.js");

/**
 * NRGroup - Description
 * @property {string} group - parent group, if set
 * @extends NRContainer
 */
class NRGroup extends NRContainer {

    /**
     * constructor - Description
     *
     * @param {type} config Description
     */
    constructor(config) {
        super(config);
        this.w = config.w;
        this.h = config.h;
        delete config.w;
        delete config.h;

        this.group = config.g;
        delete config.g;
    }

    export() {
        let obj = super.export();
        obj.w = this.w;
        obj.h = this.h;
        if (this.group) {
            obj.g = this.group;
        }
        return obj;
    }
}

module.exports = NRGroup
